/* View Controls */
.projects-controls {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 1.5rem;
    margin-bottom: 1.5rem;
}

.projects-view-toggle {
    display: flex;
    gap: 0.5rem;
}

.view-toggle-btn {
    padding: 0.4rem 0.9rem;
    font-size: 0.75rem;
    letter-spacing: 0.12em;
    text-transform: uppercase;
    border: 1px solid rgba(200, 191, 173, 0.8);
    border-radius: 8px;
    background: transparent;
    color: var(--clay);
    cursor: pointer;
    transition: all 0.2s ease;
}

.view-toggle-btn:hover {
    border-color: var(--storm);
    color: var(--storm);
}

.view-toggle-btn.active {
    background: var(--storm);
    color: white;
    border-color: var(--storm);
}

/* Metadata badges in timeline */
.project-node-metadata {
    display: flex;
    gap: 0.6rem;
    flex-wrap: wrap;
    margin: 0.5rem 0;
}

.metadata-badge {
    display: inline-flex;
    align-items: center;
    gap: 0.35rem;
    padding: 0.35rem 0.65rem;
    background: rgba(196, 137, 58, 0.08);
    border: 1px solid rgba(196, 137, 58, 0.4);
    border-radius: 6px;
    font-size: 0.75rem;
    color: var(--storm);
}

.badge-icon {
    font-size: 0.85rem;
}

.badge-value {
    font-weight: 500;
}

/* Grid View */
.projects-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
    gap: 1.4rem;
}

.grid-card {
    padding: 1.3rem 1.2rem 1.4rem;
    border-radius: 18px;
    border: 1px solid rgba(200, 191, 173, 0.95);
    background: rgba(232, 226, 217, 0.96);
    box-shadow: 0 14px 32px rgba(26, 24, 20, 0.06);
    display: flex;
    flex-direction: column;
    gap: 0.8rem;
    transition: all 0.3s ease;
}

.grid-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 18px 40px rgba(26, 24, 20, 0.1);
}

.grid-card--highlight {
    border-color: rgba(196, 137, 58, 0.4);
    background: rgba(232, 226, 217, 0.99);
}

.grid-card-header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    gap: 0.6rem;
}

.grid-card-title {
    font-family: "Playfair Display", serif;
    font-size: 1.15rem;
    margin: 0;
    color: var(--storm);
    line-height: 1.2;
}

.grid-card-description {
    font-size: 0.82rem;
    line-height: 1.7;
    color: var(--storm);
    margin: 0;
    flex-grow: 1;
}

.grid-card-meta {
    display: grid;
    gap: 0.6rem;
}

.meta-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 0.8rem;
    padding: 0.5rem 0;
    border-top: 1px solid rgba(200, 191, 173, 0.3);
}

.meta-label {
    font-size: 0.65rem;
    letter-spacing: 0.12em;
    text-transform: uppercase;
    color: var(--clay);
    font-weight: 600;
}

.meta-value {
    font-size: 0.8rem;
    color: var(--storm);
}

.grid-card-badges {
    display: flex;
    gap: 0.6rem;
    flex-wrap: wrap;
}

.grid-badge {
    display: inline-block;
    padding: 0.3rem 0.6rem;
    background: rgba(196, 137, 58, 0.1);
    border: 1px solid rgba(196, 137, 58, 0.3);
    border-radius: 6px;
    font-size: 0.72rem;
    color: var(--storm);
}

.grid-card-tags {
    margin-top: 0.3rem;
}

.grid-card-links {
    display: flex;
    gap: 0.6rem;
    flex-wrap: wrap;
    margin-top: 0.4rem;
}

/* List View */
.projects-list {
    display: flex;
    flex-direction: column;
    gap: 0.1rem;
}

.list-header {
    display: grid;
    grid-template-columns: 2fr 1.2fr 1.2fr 1fr;
    gap: 1rem;
    padding: 0.8rem 1rem;
    background: rgba(232, 226, 217, 0.5);
    border: 1px solid rgba(200, 191, 173, 0.6);
    border-radius: 12px 12px 0 0;
    font-size: 0.65rem;
    letter-spacing: 0.12em;
    text-transform: uppercase;
    color: var(--clay);
    font-weight: 600;
}

.list-row {
    display: grid;
    grid-template-columns: 2fr 1.2fr 1.2fr 1fr;
    gap: 1rem;
    padding: 0.9rem 1rem;
    border: 1px solid rgba(200, 191, 173, 0.4);
    border-top: none;
    background: rgba(232, 226, 217, 0.96);
    transition: all 0.2s ease;
    align-items: center;
}

.list-row:last-child {
    border-radius: 0 0 12px 12px;
}

.list-row:hover {
    background: rgba(232, 226, 217, 0.99);
    border-color: rgba(200, 191, 173, 0.6);
}

.list-row--highlight {
    background: rgba(232, 226, 217, 0.99);
    border-color: rgba(196, 137, 58, 0.3);
}

.list-col-title {
    position: relative;
    display: flex;
    flex-direction: column;
    gap: 0.3rem;
}

.list-title-main {
    font-family: "Playfair Display", serif;
    font-size: 0.95rem;
    font-weight: 600;
    color: var(--storm);
    line-height: 1.2;
}

.list-title-meta {
    font-size: 0.72rem;
    line-height: 1.4;
    color: var(--clay);
    display: -webkit-box;
    -webkit-line-clamp: 1;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.list-chip {
    position: absolute;
    top: -0.8rem;
    right: 0;
    font-size: 0.55rem;
}

.list-col-stack {
    display: flex;
    align-items: center;
}

.stack-chip {
    display: inline-block;
    padding: 0.35rem 0.65rem;
    background: rgba(196, 137, 58, 0.1);
    border: 1px solid rgba(196, 137, 58, 0.3);
    border-radius: 6px;
    font-size: 0.75rem;
    color: var(--storm);
    font-weight: 500;
}

.list-col-meta {
    display: flex;
    gap: 0.4rem;
    flex-wrap: wrap;
}

.stat-badge {
    display: inline-block;
    padding: 0.25rem 0.5rem;
    background: rgba(143, 175, 194, 0.1);
    border: 1px solid rgba(143, 175, 194, 0.3);
    border-radius: 4px;
    font-size: 0.7rem;
    color: var(--storm);
}

.list-col-links {
    display: flex;
    gap: 0.5rem;
    flex-wrap: wrap;
}

/* Responsive Grid and List Views */
@media (max-width: 1200px) {
    .projects-grid {
        grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    }

    .list-header,
    .list-row {
        grid-template-columns: 1.8fr 1fr 1fr 0.8fr;
    }
}

@media (max-width: 900px) {
    .projects-intro-card {
        grid-template-columns: 1fr;
    }

    .projects-controls {
        flex-direction: column;
        align-items: flex-start;
    }

    .projects-grid {
        grid-template-columns: repeat(auto-fill, minmax(260px, 1fr));
    }

    .list-header,
    .list-row {
        grid-template-columns: 1.5fr 1fr 1fr 0.7fr;
        gap: 0.8rem;
        padding: 0.7rem 0.8rem;
    }

    .list-title-meta {
        font-size: 0.68rem;
    }
}

