    -webkit-line-clamp: 1;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.list-chip {
    position: absolute;
    top: -0.8rem;
    right: 0;
    font-size: 0.55rem;
}

.list-col-stack {
