    align-items: center;
}

.stack-chip {
    display: inline-block;
    padding: 0.35rem 0.65rem;
    background: rgba(196, 137, 58, 0.1);
    border: 1px solid rgba(196, 137, 58, 0.3);
    border-radius: 6px;
    font-size: 0.75rem;
    color: var(--storm);
    font-weight: 500;
}

.list-col-meta {
    display: flex;
    gap: 0.4rem;
    flex-wrap: wrap;
}

.stat-badge {
    display: inline-block;
    padding: 0.25rem 0.5rem;
    background: rgba(143, 175, 194, 0.1);
    border: 1px solid rgba(143, 175, 194, 0.3);
    border-radius: 4px;
    font-size: 0.7rem;
    color: var(--storm);
}

.list-col-links {
    display: flex;
    gap: 0.5rem;
    flex-wrap: wrap;
}

/* Responsive Grid and List Views */
@media (max-width: 1200px) {
    .projects-grid {
        grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    }

    .list-header,
    .list-row {
        grid-template-columns: 1.8fr 1fr 1fr 0.8fr;
    }
}

@media (max-width: 900px) {
    .projects-intro-card {
        grid-template-columns: 1fr;
