    margin-bottom: 0.7rem;
}

/* Timeline layout */
.projects-timeline {
    position: relative;
    padding-left: 1.3rem;
    border-left: 1px solid rgba(200, 191, 173, 0.8);
    display: flex;
    flex-direction: column;
    gap: 1.4rem;
}

/* Single project node */
.project-node {
    position: relative;
    display: flex;
    gap: 1.2rem;
}

.project-node-dot {
    position: absolute;
    left: -1.1rem;
    top: 0.9rem;
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: var(--ochre);
    box-shadow: 0 0 0 3px rgba(196, 137, 58, 0.25);
}

.project-node--highlight .project-node-dot {
    background: var(--storm);
    box-shadow: 0 0 0 4px rgba(143, 175, 194, 0.6);
}

.project-node-content {
    flex: 1;
    padding: 1.3rem 1.2rem 1.4rem;
    border-radius: 18px;
    border: 1px solid rgba(200, 191, 173, 0.95);
    background: rgba(232, 226, 217, 0.96);
    box-shadow: 0 14px 32px rgba(26, 24, 20, 0.06);
}

/* Header inside node */
.project-node-header {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    gap: 0.75rem;
    margin-bottom: 0.4rem;
}

.project-node-title-block {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    gap: 0.45rem;
}

.project-node-title {
    font-family: "Playfair Display", serif;
    font-size: 1.25rem;
    margin: 0;
    color: var(--storm);
}

.project-node-chip {
    font-size: 0.6rem;
    letter-spacing: 0.18em;
    text-transform: uppercase;
    padding: 0.15rem 0.55rem;
    border-radius: 999px;
    border: 1px solid rgba(196, 137, 58, 0.9);
    color: var(--ochre);
    background: rgba(196, 137, 58, 0.08);
}

.project-node-date {
    font-size: 0.7rem;
    letter-spacing: 0.16em;
    text-transform: uppercase;
    color: var(--clay);
}

/* Body text */
.project-node-text {
    font-size: 0.85rem;
    line-height: 1.9;
    color: var(--storm);
    margin: 0.4rem 0 0.8rem;
}

/* Meta info */
.project-node-meta {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(170px, 1fr));
    gap: 0.6rem 1.2rem;
    margin: 0 0 0.9rem;
}

.project-node-meta div {
    display: flex;
    flex-direction: column;
    gap: 0.1rem;
}

.project-node-meta dt {
    font-size: 0.63rem;
    letter-spacing: 0.16em;
    text-transform: uppercase;
    color: var(--clay);
}

.project-node-meta dd {
    margin: 0;
    font-size: 0.8rem;
    color: var(--storm);
}

/* Footer inside node */
.project-node-footer {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: flex-end;
    gap: 0.8rem;
}

.project-node-tags {
    flex-wrap: wrap;
}

.project-node-links {
    display: flex;
    flex-wrap: wrap;
    gap: 0.6rem;
}

/* Responsive */
@media (max-width: 900px) {
    .projects-intro-card {
        grid-template-columns: 1fr;
    }
}

@media (max-width: 600px) {
    .projects-page {
        padding: 2rem 1.4rem 3.5rem;
    }

    .projects-title {
        font-size: clamp(1.8rem, 7vw, 2.2rem);
    }

    .projects-timeline {
        padding-left: 1rem;
    }

    .project-node-dot {
        left: -0.95rem;
    }

    .project-node-header {
        flex-direction: column;
        align-items: flex-start;
    }
}


